package com.tns.sample;

import java.util.Scanner;

public class Multi {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		Scanner s=new Scanner(System.in);
		int n=s.nextInt();
		int b=3;
		System.out.print(b+" ");
		for(int i=1;i<n;i++) {
			 b=b*3;
			 System.out.print(b+" ");
			 
			 
			
		}
		s.close();

	}

}
